function pageContentDropcap(){var a=document.querySelector("p[data-dropcap]");if(!a)return null;var e=a.textContent;e=(e=(e=e.trim()).split(" ")).map((function(a,e){return 0===e?'<span class="dropcap" role="text">'+a+"</span>":a})),a.innerHTML=e.join(" ");var n=a.querySelector(".dropcap").textContent,r=n.charAt(0),t=n.slice(1);if(r.match(/[a-z]/i)){var p='<span aria-hidden="true"><span class="dropcap-letter dropcap-letter-'+r.toLowerCase()+'">'+r+"</span>"+t+'</span><span class="sr-only">'+n+"</span>";a.innerHTML=a.innerHTML.replace(n,p)}}
